import React, { useState } from "react";
import ReactMarkdown from "react-markdown";
import { jsPDF } from "jspdf";
import "./App.css";

function Modal({ title, message, onClose }) {
  return (
    <div className="modal-overlay">
      <div className="modal">
        <h3>{title}</h3>
        <p>{message}</p>
        <button onClick={onClose} className="cancel-btn">Close</button>
      </div>
    </div>
  );
}

function Toast({ message }) {
  return <div className="toast">{message}</div>;
}

export default function Home() {
  const [file, setFile] = useState(null);
  const [feedback, setFeedback] = useState("");
  const [jobSuggestions, setJobSuggestions] = useState("");
  const [careerChangeTips, setCareerChangeTips] = useState("");
  const [showOverlay, setShowOverlay] = useState(false);
  const [loading, setLoading] = useState(false);
  const [showModal, setShowModal] = useState(false);
  const [modalTitle, setModalTitle] = useState("");
  const [modalMessage, setModalMessage] = useState("");
  const [showToast, setShowToast] = useState(false);
  const [toastMessage, setToastMessage] = useState("");

  const MAX_FILE_SIZE_MB = 2;

  const handleUpload = (e) => {
    const uploadedFile = e.target.files[0];
    if (!uploadedFile) return;

    const fileSizeMB = uploadedFile.size / 1024 / 1024;
    if (fileSizeMB > MAX_FILE_SIZE_MB) {
      setModalTitle("File Too Large");
      setModalMessage(`The file exceeds the maximum size limit of ${MAX_FILE_SIZE_MB}MB.`);
      setShowModal(true);
      setFile(null);
      return;
    }

    setFile(uploadedFile);
  };

  const handleSubmit = async () => {
    if (!file) {
      setModalTitle("No File Uploaded");
      setModalMessage("Please upload a resume before submitting.");
      setShowModal(true);
      return;
    }

    const formData = new FormData();
    formData.append("resume", file);
    setShowOverlay(true);
    setLoading(true);

    try {
      const res = await fetch("http://localhost:5000/upload", {
        method: "POST",
        body: formData,
      });

      const data = await res.json();

      if (data.error === "NOT_RESUME") {
        setModalTitle("Invalid Document");
        setModalMessage("This document doesn‚Äôt look like a resume. Please upload a valid resume file.");
        setShowModal(true);
        return;
      }

      setFeedback(data.feedback);
      setJobSuggestions(data.jobSuggestions);
      setCareerChangeTips(data.careerChangeTips);
      setToastMessage("Resume uploaded and analyzed successfully!");
      setShowToast(true);
      setTimeout(() => setShowToast(false), 4000);
    } catch (err) {
      console.error(err);
      setModalTitle("Upload Failed");
      setModalMessage("There was an error processing your resume. Please try again.");
      setShowModal(true);
    } finally {
      setLoading(false);
      setShowOverlay(false);
    }
  };

  const handleDownloadPDF = () => {
    const doc = new jsPDF();
    const margin = 15;
    const lineHeight = 10;
    let y = margin;

    const sections = [
      { title: "üìù AI Feedback", content: feedback },
      { title: "üíº Job Suggestions", content: jobSuggestions },
      { title: "üí° Career Change Tips", content: careerChangeTips },
    ];

    doc.setFontSize(14);

    sections.forEach(({ title, content }) => {
      doc.setFont("helvetica", "bold");
      doc.text(title, margin, y);
      y += lineHeight;

      doc.setFont("helvetica", "normal");
      const lines = doc.splitTextToSize(content, 180);
      lines.forEach(line => {
        if (y > 280) {
          doc.addPage();
          y = margin;
        }
        doc.text(line, margin, y);
        y += lineHeight;
      });

      y += lineHeight;
    });

    doc.save("Resume-AI-Feedback.pdf");
  };

  // üëá Custom render for job suggestions with buttons
  const renderJobSuggestions = (markdown) => {
    const lines = markdown.split("\n").filter(line => line.trim() !== "");
    const suggestions = [];

    let currentTitle = null;
    let currentBullets = [];

    lines.forEach(line => {
      if (/^\d+\.\s/.test(line)) {
        if (currentTitle) {
          suggestions.push({ title: currentTitle, bullets: currentBullets });
        }
        currentTitle = line.replace(/^\d+\.\s*/, "").trim();
        currentBullets = [];
      } else if (line.startsWith("-")) {
        currentBullets.push(line);
      }
    });

    if (currentTitle) {
      suggestions.push({ title: currentTitle, bullets: currentBullets });
    }

    return (
      <div className="job-suggestion-list">
        {suggestions.map((job, idx) => {
          const jobTitle = job.title;
          const jobQuery = `${jobTitle} jobs near me`;
          const jobUrl = `https://www.google.com/search?q=${encodeURIComponent(jobQuery)}`;

          return (
            <div key={idx} className="markdown-box">
              <h3>{jobTitle}</h3>
              <ul>
                {job.bullets.map((bullet, i) => (
                  <li key={i}>{bullet.replace(/^-/, "").trim()}</li>
                ))}
              </ul>
              <a
                href={jobUrl}
                target="_blank"
                rel="noopener noreferrer"
                className="view-jobs-btn"
              >
                üîç View Jobs
              </a>
            </div>
          );
        })}
      </div>
    );
  };

  return (
    <>
      {showOverlay && (
        <div className="overlay">
          <div className="overlay-content">
            <img src="/scan-icon.png" alt="Scanning" className="scan-icon" />
            <h3>Scanning Resume...</h3>
            <p>Your resume is being analyzed for ATS compatibility.</p>
            <button className="cancel-btn" onClick={() => setShowOverlay(false)}>Cancel</button>
          </div>
        </div>
      )}

      {showModal && (
        <Modal
          title={modalTitle}
          message={modalMessage}
          onClose={() => setShowModal(false)}
        />
      )}

      {showToast && <Toast message={toastMessage} />}

      <header className="app-header">
        <h1>Smart AI Resume Analyzer</h1>
        <p className="subtitle">Built with üíú by Hextech</p>
      </header>

      <main className="main-card">
        <div className="upload-bar">
          <input type="file" accept=".pdf,.docx" onChange={handleUpload} />
          <button onClick={handleSubmit}>üöÄ Analyze</button>
        </div>
        <p className="upload-note">Max upload size: 2MB. Supported formats: .pdf, .docx</p>

        {loading && <p className="loading">‚è≥ Analyzing your resume...</p>}

        {!loading && feedback && (
          <>
            <section>
              <h2>üìù AI Feedback</h2>
              <div className="markdown-box">
                <ReactMarkdown>{feedback}</ReactMarkdown>
              </div>
            </section>

            <section>
              <h2>üíº Job Scope Suggestions</h2>
              {renderJobSuggestions(jobSuggestions)}
            </section>

            <section>
              <h2>üí° Career Change Tips</h2>
              <div className="markdown-box">
                <ReactMarkdown>{careerChangeTips}</ReactMarkdown>
              </div>
            </section>

            <button onClick={handleDownloadPDF} className="download-btn">üìÑ Download PDF</button>
          </>
        )}
      </main>

      <footer className="footer">
        <p>¬© 2025 RightJob ‚Ä¢ Built with üíú for your career</p>
      </footer>
    </>
  );
}
