import React, { useState } from "react";
import ReactMarkdown from "react-markdown";

export default function Home() {
  const [file, setFile] = useState(null);
  const [feedback, setFeedback] = useState("");
  const [jobSuggestions, setJobSuggestions] = useState("");
  const [careerChangeTips, setCareerChangeTips] = useState("");
const [jobTitles, setJobTitles] = useState([]);
const [selectedJobTitle, setSelectedJobTitle] = useState("");
const [jobListings, setJobListings] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleUpload = (e) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = async () => {
    if (!file) return alert("Please upload a file.");
    setLoading(true);
    const formData = new FormData();
    formData.append("resume", file);

    try {
      const res = await fetch("http://localhost:5000/upload", {
        method: "POST",
        body: formData,
      });

      const data = await res.json();
      setFeedback(data.feedback);
      setJobSuggestions(data.jobSuggestions);
      setCareerChangeTips(data.careerChangeTips);
      setJobListings(data.jobListings);
    } catch (err) {
      alert("Error processing resume.");
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <header className="app-header">
        <h1>Smart AI Resume Analyzer</h1>
        <p className="subtitle">**</p>
      </header>

      <main className="main-card">
        <div className="upload-bar">
          <input type="file" accept=".pdf,.docx" onChange={handleUpload} />
          <button onClick={handleSubmit}>üöÄ Analyze</button>
        </div>

        {loading && <p className="loading">‚è≥ Analyzing your resume...</p>}

        {!loading && feedback && (
          <section>
            <h2>üìù AI Feedback</h2>
            <div className="markdown-box">
              <ReactMarkdown>{feedback}</ReactMarkdown>
            </div>
          </section>
        )}

        {!loading && jobSuggestions && (
          <section>
            <h2>üíº Job Scope Suggestions</h2>
            <div className="markdown-box">
              <ReactMarkdown>{jobSuggestions}</ReactMarkdown>
            </div>
          </section>
        )}
        {!loading && careerChangeTips && (
          <section>
            <h2>üí° Career Change Tips</h2>
            <div className="markdown-box">
              <ReactMarkdown>{careerChangeTips}</ReactMarkdown>
            </div>
          </section>
        )}
        {!loading && jobListings.length > 0 && (
          <section>
            <h2>üåê Live Job Listings</h2>
            <div className="markdown-box">
              <ul>
                {jobListings.map((job, index) => (
                  <li key={index}>
                    <strong>{job.title}</strong> at {job.company} <br />
                    üìç {job.location} <br />
                    <a href={job.url} target="_blank" rel="noopener noreferrer">View Job</a>
                  </li>
                ))}
              </ul>
            </div>
          </section>
        )}


      </main>

      <footer className="footer">
        <p>¬© 2025 RightJob ‚Ä¢ Built with üíú for your career</p>
      </footer>
    </>
  );
}
