import React, { useState } from "react";
import ReactMarkdown from "react-markdown";
import "./App.css";

export default function App() {
  const [file, setFile] = useState(null);
  const [feedback, setFeedback] = useState("");
  const [jobSuggestions, setJobSuggestions] = useState("");
  const [loading, setLoading] = useState(false);

  const handleUpload = (e) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = async () => {
    if (!file) return alert("Please upload a file.");
    setLoading(true);
    const formData = new FormData();
    formData.append("resume", file);

    try {
      const res = await fetch("http://localhost:5000/upload", {
        method: "POST",
        body: formData,
      });

      const data = await res.json();
      setFeedback(data.feedback);
      setJobSuggestions(data.jobSuggestions);
    } catch (err) {
      alert("Error processing resume. Please try again.");
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="page-wrapper">
      {/* Navigation Bar */}
      <nav className="navbar">
        <div className="navbar-left">
          <img src="/rightjob-logo.png" alt="Logo" className="navbar-logo" />
        </div>
        <div className="navbar-right">
          <a href="#home">Home</a>
          <a href="#features">Features</a>
          <a href="#about">About</a>
        </div>
      </nav>

      {/* Main Header */}
      <header className="app-header" id="home">
        <h1>Smart AI Resume Analyzer</h1>
        <p className="subtitle"></p>
      </header>

      {/* Main Content */}
      <main className="main-card" id="features">
        <div className="upload-bar">
          <input type="file" accept=".pdf,.docx" onChange={handleUpload} />
          <button onClick={handleSubmit}>üöÄ Analyze</button>
        </div>

        {loading && <p className="loading">‚è≥ Analyzing your resume...</p>}

        {!loading && feedback && (
          <section>
            <h2>üìù AI Feedback</h2>
            <div className="markdown-box">
              <ReactMarkdown>{feedback}</ReactMarkdown>
            </div>
          </section>
        )}

        {!loading && jobSuggestions && (
          <section>
            <h2>üíº Job Scope Suggestions</h2>
            <div className="markdown-box">
              <ReactMarkdown>{jobSuggestions}</ReactMarkdown>
            </div>
          </section>
        )}
      </main>

      {/* Footer */}
      <footer className="footer" id="about">
        <p>¬© 2025 RightJob ‚Ä¢ Built with üíú for your career</p>
      </footer>
    </div>
  );
}
