import React, { useState } from "react";
import ReactMarkdown from "react-markdown";

export default function Home() {
  const [file, setFile] = useState(null);
  const [feedback, setFeedback] = useState("");
  const [jobSuggestions, setJobSuggestions] = useState("");
  const [careerChangeTips, setCareerChangeTips] = useState("");
  const [jobTitles, setJobTitles] = useState([]);
  const [selectedJobTitle, setSelectedJobTitle] = useState("");
  const [jobListings, setJobListings] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleUpload = (e) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = async () => {
    if (!file) return alert("Please upload a file.");
    setLoading(true);
    const formData = new FormData();
    formData.append("resume", file);

    try {
      const res = await fetch("http://localhost:5000/upload", {
        method: "POST",
        body: formData,
      });

      const data = await res.json();
      setFeedback(data.feedback);
      setJobSuggestions(data.jobSuggestions);
      setCareerChangeTips(data.careerChangeTips);
      const titles = data.jobSuggestions
        .split("\n")
        .filter(line => /^[0-9]/.test(line))
        .map(line => line.replace(/^[0-9\.\-)]*\s*/, "").split(" - ")[0].trim());

      setJobTitles(titles);
      setSelectedJobTitle(titles[0] || ""); // default selected

      setJobListings(data.jobListings);
    } catch (err) {
      alert("Error processing resume.");
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <header className="app-header">
        <h1>Smart AI Resume Analyzer</h1>
        <p className="subtitle">**</p>
      </header>

      <main className="main-card">
        <div className="upload-bar">
          <input type="file" accept=".pdf,.docx" onChange={handleUpload} />
          <button onClick={handleSubmit}>🚀 Analyze</button>
        </div>

        {loading && <p className="loading">⏳ Analyzing your resume...</p>}

        {!loading && feedback && (
          <section>
            <h2>📝 AI Feedback</h2>
            <div className="markdown-box">
              <ReactMarkdown>{feedback}</ReactMarkdown>
            </div>
          </section>
        )}

        {!loading && jobSuggestions && (
          <section>
            <h2>💼 Job Scope Suggestions</h2>
            <div className="markdown-box">
              <ReactMarkdown>{jobSuggestions}</ReactMarkdown>
            </div>
          </section>
        )}
        {!loading && careerChangeTips && (
          <section>
            <h2>💡 Career Change Tips</h2>
            <div className="markdown-box">
              <ReactMarkdown>{careerChangeTips}</ReactMarkdown>
            </div>
          </section>
        )}
        {!loading && jobTitles.length > 0 && (
          <section>
            <h2>🌐 Get Live Jobs</h2>
            <div className="upload-bar">
              <select value={selectedJobTitle} onChange={(e) => setSelectedJobTitle(e.target.value)}>
                {jobTitles.map((title, index) => (
                  <option key={index} value={title}>{title}</option>
                ))}
              </select>
              <button onClick={fetchJobListings}>🔎 Search Jobs</button>
            </div>
          </section>
        )}

      </main>

      <footer className="footer">
        <p>© 2025 RightJob • Built with 💜 for your career</p>
      </footer>
    </>
  );
}
