import React, { useState } from "react";

function App() {
  const [file, setFile] = useState(null);
  const [feedback, setFeedback] = useState("");
  const [jobSuggestions, setJobSuggestions] = useState("");

  const handleUpload = (e) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = async () => {
    if (!file) return alert("Please upload a file.");
    const formData = new FormData();
    formData.append("resume", file);

    const res = await fetch("http://localhost:5000/upload", {
      method: "POST",
      body: formData,
    });

    const data = await res.json();
    setFeedback(data.feedback);
    setJobSuggestions(data.jobSuggestions);
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>RightJob - Resume Analyzer</h1>
      <input type="file" accept=".pdf,.docx" onChange={handleUpload} />
      <button onClick={handleSubmit}>Submit Resume</button>

      <div style={{ marginTop: "20px" }}>
        <h2>üìù AI Feedback</h2>
        <pre>{feedback}</pre>

        <h2>üíº Job Scope Suggestions</h2>
        <pre>{jobSuggestions}</pre>
      </div>
    </div>
  );
}

export default App;
